mapper.select:
SELECT userid
FROM mini_member
WHERE userid = #{userid}

SELECT email
FROM mini_member
WHERE email = #{email}

mapper.update:
update mini_member set
logintime = sysdate
where userid = #{userid}

update mini_member
set admin = 'Y'
where userid = #{userid}

update mini_member set
failcount = #{failcount},
locked = #{locked}
where userid= #{userid}

mapper.insert:
insert into mini_member (
usernumber,userid, passwd, name,
age,email,phonenumber,address,detailaddress,sex,habit,registerTime,birthday
) values (
seq_mini_number.NEXTVAL,#{userid}, #{passwd}, #{name}, #{age},#{email},#{phonenumber}
,#{address}
,#{detailaddress}
,#{sex}
,#{habit}
,sysdate
,TO_DATE(#{birthday}, 'YYYY-MM-DD')
)

mapper.delete:
update mini_member
set deleteyn = 'Y'
where userid = #{userid}

update mini_member
set deleteyn = 'N'
where userid = #{userid}
