mapper.update:
update mini_board
set deleteyn = 'Y'
where bno = #{bno}

mapper.insert:
		insert into mini_board(
		bno,btitle,bcontent,bwriter,bdate,bpasswd
		)
		values(
		seq_mini_board.NEXTVAL,#{btitle}, #{bcontent}, #{bwriter},
		sysdate,#{bpasswd}

		)

mapper.update:
update mini_board
set view_cnt = view_cnt+1
where bno = #{bno}
